mapper.update:
update cp_board
set  board_subject = #{subject}
	,board_state = #{state}
	,board_content = #{content}
	,board_uptdate = null
	,board_passer = null
where board_seq = #{boardSeq}

update cp_board
set  board_subject = #{subject}
	
		
		,board_uptdate = null
		,board_passer = null
		
		
		,board_uptdate = sysdate
		,board_passer = #{memId}
		
	
	,board_state = #{state}
	,board_content = #{content}
where board_seq = #{boardSeq}

mapper.insert:
insert into cp_log (
					 log_seq
					,log_uptdate
					,log_passer
					,log_state
					,log_writer
					,board_seq
					)
			values (
					 (select nvl(max(log_seq),0)+1 from cp_log where board_seq = #{boardSeq})
					 ,sysdate
					 ,#{boardWriter}
					 ,#{boardState}
					 ,#{boardWriter}
					 ,#{boardSeq}
					 )

insert into cp_log (
					 log_seq
					,log_uptdate
					,log_passer
					,log_state
					,log_writer
					,board_seq
					)
			values (
					 (select nvl(max(log_seq),0)+1 from cp_log where board_seq = #{boardSeq})
					 ,sysdate
					 ,#{memId}
					 ,#{state}
					 ,#{boardWriter}
					 ,#{boardSeq}
					 )

mapper.update:
